import './Body.css';

function Body(props){
    return(
        <div>
            <div className="bodyEncabezado">
                <p>{props.tituloPractica}</p>
            </div>
            <div className="bodyEstilo">
                {props.arreglo.map((e,index)=>
                <p key={index}>{index+1}. {e}</p>)}
            </div>
            <div className="bodyPie">
                <button name="alerta" onClick="funcion()">Â¡Haz clic!</button>
            </div>

            <script>
                function funcion(){
                    alert("Soy sofia, saludos")
                }
            </script>
        </div>
    );
}

export default Body;
